    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        BDVeiculos bdVeiculos2 = this.getBdVeiculos();
        Carga carga = new Carga();
        String novaPlaca;
        try {
            carga.setCargaMax(Integer.parseInt(jTextField10.getText()));
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Valor invalido no Carga Maxima", "ERRO", 0);
            return;
        }
        try {
            carga.setTara(Integer.parseInt(jTextField1.getText()));
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Valor invalido na Tara", "ERRO", 0);
            return;
        }

        try {
            novaPlaca = bdVeiculos2.verificaPlaca(jTextField2.getText());
        } catch (VeicExistException vee) {
            JOptionPane.showMessageDialog(null, "Ja existe essa placa cadastrada", "ERRO", 0);
            jTextField1.setText("");
            jTextField2.setText("");
            jTextField3.setText("");
            jTextField4.setText("");
            jTextField5.setText("");
            jTextField6.setText("");
            jTextField7.setText("");
            jTextField8.setText("");
            jTextField9.setText("");
            jTextField10.setText("");
            jTextField1.requestFocus();
            return;
        }
        carga.setPlaca(novaPlaca);
        try {
            carga.setMarca(jTextField3.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Valor invalido na Marca", "ERRO", 0);
            return;
        }
        try {
            carga.setModelo(jTextField4.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Valor invalido na Modelo", "ERRO", 0);
            return;
        }
        try {
            carga.setCor(jTextField5.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Valor invalido na Cor", "ERRO", 0);
            return;
        }
        try {
            carga.setVelocMax(Integer.parseInt(jTextField7.getText()));
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Valor invalido no Velocidade Maxima", "ERRO", 0);
            return;
        } catch (VelocException ve) {
            try {
                carga.setVelocMax(90);
            } catch (VelocException ex) {
                JOptionPane.showMessageDialog(null, "Erro", "Erro", JOptionPane.ERROR);
            }
        }
        try {
            carga.setQtdRodas(Integer.parseInt(jTextField6.getText()));
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Valor invalido no Qtd Rodas", "ERRO", 0);
            return;
        }
        int qtdPist;
        int potencia;
        try {
            qtdPist = Integer.parseInt(jTextField9.getText());
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Valor invalido no qtdPist", "ERRO", 0);
            return;
        }
        try {
            potencia = Integer.parseInt(jTextField8.getText());
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Valor invalido na Potencia", "ERRO", 0);
            return;
        }
        carga.setMotor(qtdPist, potencia);
        bdVeiculos2.getCargas().add(carga);
        JOptionPane.showMessageDialog(null, "Carro de Carga cadastrado com sucesso!", "Ok", 1);
        jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField4.setText("");
        jTextField5.setText("");
        jTextField6.setText("");
        jTextField7.setText("");
        jTextField8.setText("");
        jTextField9.setText("");
        jTextField10.setText("");
        jTextField1.requestFocus();
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        int resp = JOptionPane.showConfirmDialog(null, "Deseja sair?", "Saida do sistema", -1);

        if (resp == 0) {
            dispose();
        }
    }//GEN-LAST:event_jButton5ActionPerformed

