    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        String placa = jTextField11.getText();
        boolean placaExiste = false;
        if (bdVeiculos.getPasseios().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Carro de Passeio vazio", "ERRO", 0);
        } else {
            for (int i = 0; i < bdVeiculos.getPasseios().size(); i++) {
                if (placa.equals(bdVeiculos.getPasseios().get(i).getPlaca())) {
                    placaExiste = true;
                    jTextField3.setText(bdVeiculos.getPasseios().get(i).getMarca());
                    jTextField4.setText(bdVeiculos.getPasseios().get(i).getModelo());
                    jTextField5.setText(bdVeiculos.getPasseios().get(i).getCor());
                    jTextField6.setText(String.valueOf(bdVeiculos.getPasseios().get(i).getQtdRodas()));
                    jTextField7.setText(String.valueOf(bdVeiculos.getPasseios().get(i).getVelocMax()));
                    jTextField8.setText(String.valueOf(bdVeiculos.getPasseios().get(i).getMotor().getQtdPist()));
                    jTextField9.setText(String.valueOf(bdVeiculos.getPasseios().get(i).getMotor().getPotencia()));
                    jTextField12.setText(String.valueOf(bdVeiculos.getPasseios().get(i).getQtdPassageiros()));

                } else if (i + 1 == bdVeiculos.getPasseios().size() && !placaExiste) {
                    JOptionPane.showMessageDialog(null, "Veiculo com essa placa não existe", "ERRO", 0);
                }
            }
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        int resp = JOptionPane.showConfirmDialog(null, "Deseja sair?", "Saida do sistema", -1);

        if (resp == 0) {
            dispose();
        }
    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        String placa = jTextField11.getText();
        boolean placaExiste = false;
        if (bdVeiculos.getPasseios().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Carro de Passeio vazio", "ERRO", 0);
            jTextField11.setText("");
        } else {
            for (int i = 0; i < bdVeiculos.getPasseios().size(); i++) {
                if (placa.equals(bdVeiculos.getPasseios().get(i).getPlaca())) {
                    placaExiste = true;
                    bdVeiculos.getPasseios().remove(i);
                    jTextField3.setText("");
                    jTextField4.setText("");
                    jTextField5.setText("");
                    jTextField6.setText("");
                    jTextField7.setText("");
                    jTextField8.setText("");
                    jTextField9.setText("");
                    jTextField12.setText("");
                    jTextField11.requestFocus();
                    JOptionPane.showMessageDialog(null, "Carro de Passeio excluido com sucesso!", "Ok", 1);
                } else if (i + 1 == bdVeiculos.getPasseios().size() && !placaExiste) {
                    JOptionPane.showMessageDialog(null, "Veiculo de Passeio com essa placa não existe", "ERRO", 0);
                    jTextField11.setText("");
                }
            }
        }
    }//GEN-LAST:event_jButton6ActionPerformed

